    // set credentials for ClientLogin authentication
    $user = "cmp@tvcultura.com.br";
    $pass = "alipio@28042011";

    //die($this->getSpreadsheetId()."<br>".$this->getWorksheetId());

    try {
      // connect to API
      $service = Zend_Gdata_Spreadsheets::AUTH_SERVICE_NAME;
